import React, { Component } from 'react';
import{Link}from '../routes';

import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Container from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import {Card} from 'react-bootstrap';

import web3 from '../ethereum/web3'
import {Router}from '../routes';
import vote from '../ethereum/vote';
import registry from '../ethereum/registry';

class Vote extends Component {
    constructor(props) {
        super(props);
        this.state ={
          loading : false,
          loading2: false,
          search:'',
          errorMessage:''
        };
      //console.log(props.query.address);//擷取這個網址的url那part的address(from routes??)  
      this.refresh_search = this.refresh_search.bind(this);
      }
    static async getInitialProps(props){
        const{address} = props.query;
        return{address};
    }
    refresh_search(){
        Router.pushRoute(`/admin/${this.state.search}`);
        console.log(this.props.address);
    }
    render() {
        return(
        <>
         <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css" integrity="sha384-9aIt2nRpC12Uk9gS9baDl411NQApFmC26EwAOH8WgZl5MYYxFfc+NcPb1dKGj7Sk" crossOrigin="anonymous"/>
         <Container>
         <Navbar bg="dark" variant="dark"style={{width:"100%"}}>
            <Navbar.Brand ><Link route={"/vote/"+this.props.address }  ><a style={{color: "white", width:"100px"}}>Vote</a></Link></Navbar.Brand>
             <Nav className="mr-auto" style={{width:"50%"}}>
                <Link route={"/home/"+this.props.address }  ><a style={{color: "white", width:"100px"}}>Home</a></Link>
                <Link route={"/status/"+this.props.address } ><a style={{color: "white", width:"100px"}}>Status</a></Link>
                <Link route={"/admin/"+this.props.address }  ><a style={{color: "white", width:"100px"}}>CreateVote</a></Link>
                <Link route={"/index" }  ><a style={{color: "white", width:"100px"}}>Logout</a></Link>
             </Nav>
            
        </Navbar>
        <div style={{width: '100%'}}>
        <Form inline style={{ width: '33%' , margin: 'auto' , marginTop : "2%"}}>
            <div style={{color : "black"}} > inesrt your vote contract address here  -&gt;   </div>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="outline-info" onClick={this.refresh_search}>Search</Button>
            </Form>
        <Card style={{ width: '33%' , margin: 'auto' , marginTop : "2%"}}>
            <Card.Body>
                <Card.Title>Election</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">voter address : 0xjxijwijswiw</Card.Subtitle>
                <Card.Text>
                    View the latest 2020 presidential polls and head-to-head match-up between Joe Biden and Donald Trump. For more information, view cnn.com/election.
                </Card.Text>
                <Button variant="outline-info"style={{margin :"2%"}}>register</Button>
                <Button variant="outline-info"style={{margin :"2%"}}><Link route="/status" ><a>Vote</a></Link></Button>
                <Button variant="outline-info" style={{margin :"2%"}}><Link route="/status" ><a>detail candidate information</a></Link></Button>
            </Card.Body>
        </Card>
        </div>
        <div style={{ width: '100%'}}>
            <Form.Group style={{width:'33%', margin: 'auto', marginTop : "2%"}}>
            <Form.Control as="select" size="lg">
                <option>Korean Fish</option>
                <option>Donald Trump</option>
                <option>Xi DADA</option>
            </Form.Control>
            
            <Form.Group id="formGridCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>

            <Button variant="primary" type="submit">
                Submit
            </Button>
            </Form.Group>
        </div>
      </Container>
        </>
    )
    }
}


export default Vote;
