//做一個頁面是沒有mailbox時要新增一個mailbox
import React, { Component } from 'react';
import{Link}from '../routes';

import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Container from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import {Card,Table} from 'react-bootstrap';
import web3 from '../ethereum/web3'
import {Router}from '../routes';
import mailbox from '../ethereum/mailbox';
import vote from '../ethereum/vote';
const header = ["#", "vote name", "vote address", "due date", "link"];



class Home extends Component {
        constructor(props) {
          super(props);
          this.state ={
           // Mb_addr :props.query.address,
            loading : false,
            loading2: false,
            errorMessage:''
          };
        }
        static async getInitialProps(props){
          const{address} = props.query;
          const accounts = await web3.eth.getAccounts();
          const Mailbox = mailbox(address);
          let join = await Mailbox.methods.return_join_list().call();
          //console.log(join);
          //console.log(join.length);
          var join_detail = [];
          for (let index = 0; index < join.length; index++) {
            let arr = [];
            let v_addr = join[index];
            let v = vote(v_addr);
            arr[0] = (index+1).toString();
            arr[1] = await v.methods.return_question().call();
            arr[2] = v_addr;
            //let t = await v.methods.return_time().call();
            //console.log(t);
            arr[3] = "today"
            arr[4] = v_addr
            join_detail[index]=arr;
          }
          
          let self = await Mailbox.methods.return_self_list().call();
          var self_detail =[];
          for (let index = 0; index < join.length; index++) {
            let arr =[];
            let v_addr = self[index];
            let v = vote(v_addr);
            arr[0] = (index+1).toString();
            arr[1] = await v.methods.return_question().call();
            arr[2] = v_addr;
            //arr[3] = await v.methods.return_time().call();
            arr[3] = "today";
            arr[4] = await v.methods.return_stage().call();//return stage 之後要set stage
            self_detail[index]=arr;
            
          }
          console.log(self_detail);
          console.log(join_detail);
          return{address,join_detail,self_detail};
      }
    
    

    render() {
      
        return(
          <>
          <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css" integrity="sha384-9aIt2nRpC12Uk9gS9baDl411NQApFmC26EwAOH8WgZl5MYYxFfc+NcPb1dKGj7Sk" crossOrigin="anonymous"/>
          <Container>
          <Navbar bg="dark" variant="dark"style={{width:"100%"}}>
             <Navbar.Brand ><Link route="/vote" ><a style={{color: "white", width:"100px"}}>Vote</a></Link></Navbar.Brand>
              <Nav className="mr-auto" style={{width:"50%"}}>
                 <Link route="/status" ><a style={{color: "white", width:"100px"}}>Status</a></Link>
                 <Link route="/admin" ><a style={{color: "white", width:"100px"}}>CreateVote</a></Link>
              </Nav>
             <Form inline>
             <div style={{color : "white"}} > inesrt your vote contract address here  -&gt;   </div>
             <FormControl type="text" placeholder="Search" className="mr-sm-2" />
             <Button variant="outline-info">Search</Button>
             </Form>
         </Navbar>
         
         <div style={{width: '100%'}}>
         <h2 style = {{width :'75%',margin:"auto",marginTop : "3%"}}>Vote List (the vote you are invited)</h2>
         <Table striped bordered hover size="sm" style = {{width :'75%',margin:"auto",marginTop : "3%"}}>
          <thead>
            <tr>
              <th>#</th>
              <th>vote name</th>
              <th>vote address</th>
              <th>due date</th>
              <th>link</th>
            </tr>
          </thead>
          <tbody >
          
            {/*
            <tr>
        <td>1</td>
        <td>should everyone wear mask on campus</td>
        <td>0x1234556778999888</td>
        <td>7/14</td>
        <td><Link route="/vote" >link</Link></td>
      </tr>
      <tr>
        <td>2</td>
        <td>should everyone wear mask on campus</td>
        <td>0x1234556778999888</td>
        <td>7/14</td>
        <td><Link route="/vote" >link</Link></td>
      </tr>
      <tr>
        <td>3</td>
        <td>should everyone wear mask on campus</td>
        <td>0x1234556778999888</td>
        <td>7/14</td>
        <td><Link route="/vote" >link</Link></td>
      </tr>*/}
          </tbody>
        </Table>
        
        </div>
        <div style={{width: '100%'}}>
        <h2 style = {{width :'75%',margin:"auto",marginTop : "3%"}}>Vote Admin List (the vote you create)</h2>
         <Table striped bordered hover size="sm" style = {{width :'75%',margin:"auto",marginTop : "3%"}}>
          <thead>
            <tr>
              <th>#</th>
              <th>vote name</th>
              <th>vote address</th>
              <th>next step due date</th>
              <th>nextstep</th>
            </tr>
          </thead>
          <tbody>
            {/* 
            <tr>
              <td>1</td>
              <td>should everyone wear mask on campus</td>
              <td>0x1234556778999888</td>
              <td>7/14</td>
              <td><Link route="/vote" >update setting</Link></td>
            </tr>
            <tr>
              <td>2</td>
              <td>should everyone wear mask on campus</td>
              <td>0x1234556778999888</td>
              <td>7/14</td>
              <td><Link route="/vote" >distribute vote contract</Link></td>
            </tr>
            <tr>
              <td>3</td>
              <td>should everyone wear mask on campus</td>
              <td>0x1234556778999888</td>
              <td>7/14</td>
              <td><Link route="/vote" >tally</Link></td>
            </tr>*/}
          </tbody>
        </Table>
        </div>
       </Container>
         </>
    )
    }
}


export default Home;
