import React, { Component } from 'react';
import { Button, Form, Input, Message } from 'semantic-ui-react';
import Layout from '../../../components/Layout';
import { Link } from '../../../routes';
import web3 from '../../../ethereum/academic/web3';
import verify from '../../../ethereum/academic/verify';

class UploadIndex extends Component {
  state = { 
    selectedFile: null,
    hashValue: '',
    newSchool: '',
    message: ''
  };

  onFileChange = event => {
    this.setState({ selectedFile: event.target.files[0] });

    const curFile = event.target.files[0];
    const reader = new FileReader();
    var that = this;
    reader.readAsText(curFile);
    reader.onload = function (e) {
      //console.log('file:', e.target.result);
      let jsonData = JSON.parse(this.result);
      //console.log(jsonData);

      // hash json
      let reader = new FileReader();
      reader.readAsArrayBuffer(curFile);
      reader.onload = function (e) {
        var wordArray = CryptoJS.lib.WordArray.create(reader.result);
        var hash = CryptoJS.SHA256(wordArray).toString();
        //this.state.hashValue = hash;
        that.setState({ hashValue: hash });
        console.log(that.state.hashValue);
      };
    };
  };

  fileData = () => {
    if (this.state.selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>File Name: {this.state.selectedFile.name}</p>
          <p>File Type: {this.state.selectedFile.type}</p>
        </div>
      );
    }
  };

  onClick = async () => {
    const accounts = await web3.eth.getAccounts();

    this.setState({ message: 'Waiting on uploading success...' })

    //console.log(this.state.hashValue);
    //await verify.methods.upload(this.hashValue).call();

    this.setState({ message: 'A certificate has been uploaded!' })
  }

  render() {
    return (
      <Layout>
        <script src="https://cdnjs.cloudflare.com/ajax/libs/crypto-js/3.1.9-1/crypto-js.js"></script>
        <h1>Upload Certificates</h1>
        <Form>
          <h3>Choose a JSON file</h3>
          <input
            type="file"
            onChange={this.onFileChange}
            accept="application/json"
            style={{ marginBottom: 20 }}
          />
          {this.fileData()}
          {/* <Link route={`/Academic/school/${this.hashValue}/success`}> */}
            <a>
              <Button
                onClick={this.onClick}
                content='Upload'
                icon='upload'
                primary={true}
                style={{ marginTop: 10, marginBottom: 20 }}
              />
            </a>
          {/* </Link> */}
          <Form.Field>
            <h3>Add new school</h3>
            <Input
              value={this.state.newSchool}
              onChange={event =>
                this.setState({ newSchool: event.target.value })}
            />
          </Form.Field>
          <a>
              <Button
                content='Add'
                icon='add'
                primary={true}
              />
            </a>
        </Form>
      </Layout>
    );
  }
}

export default UploadIndex;
